//Computes and returns the objective functions
VectorXd computeobj(float l1, float r1, float l0, float rad, float k){
  VectorXd temp(2);
  int rl = 0, thl = 0, pts = 0;
  float momval = 0, kappainv = 0;
  for(rl=0;rl<100*rad;rl++){
    for(thl=0;thl<100*(2*M_PI);thl++){
      float r = float(rl)/float(100);
      float th = float(thl)/float(100);
      // cout<<"r and th are"<<r<<"   "<<th<<endl;
      float x = (l0/2)+r*cos(th);
      float y = k+r*sin(th);
      // cout <<"Coords are \n" << x<<"   "<<y<<endl;
      temp = ikcal(l1, r1, l0, x, y);
      float th1 = temp(0), th2 = temp(1);
      momval += MoM(l1, r1, l0, th1, th2);
      kappainv += (1/kappa(l1, r1, l0, th1, th2));
      pts++;
    }
  }
    // float Area = M_PI*pow(rad, 2);
    float obj1 = momval/pts;
    float obj2 = kappainv/pts;
 //   float obj2 = 0;
    // float obj3 = Area;
    float obj3 = 0;

    VectorXd obj(3);
    obj(0) = obj1;
    obj(1) = obj2;
    obj(2) = obj3;

    return obj;
}


