MatrixXd ikcal(float l1, float r1, float l0, float x, float y){
  MatrixXd iksol(2,1);
    iksol << acos(((pow(l1,2) - pow(r1,2) + pow(x,2) + pow(y,2))*pow(pow(l1,2)*(pow(x,2) + pow(y,2)),-0.5))/2.) + atan2(l1*y,l1*x), acos(((-2*l0*x+pow(l0,2) + pow(l1,2) - pow(r1,2) + pow(x,2) + pow(y,2))*pow(pow(l1,2)*(-2*l0*x + pow(l0,2) + pow(x,2) + pow(y,2)),-0.5))/2.) +atan2(l1*y,l1*(-l0 + x));
    // cout<<"iksol"<<"\n"<<iksol<<endl;
    return iksol;
}

